mapper.select:
select p.par_id, p.par_email, p.par_password, p.par_name, r.reg_name, p.par_address, p.par_phone, p.par_intro, p.par_photo,
p.par_subdate, p.par_state, p.author from fit_partner p inner join
fit_region r on p.par_regid = r.reg_id where par_id = #{parId}

mapper.update:
update fit_partner
set par_photo = #{parPhoto}
where par_email = #{parEmail}
